import React from "react";
import { useState } from "react";


const Koti = ()=>{
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    console.log(password);
    const [cpassword, setCpassword] = useState("");

    // let pass = false, cpass =false;
 
    function submitForm (e){
        e.preventDefault();
        // if(isValidEmail && pass && cpass){
          // alert("hell")
        // }
        const emailRegex =
        new RegExp(/^[A-Za-z0-9_!#$%&'*+\/=?`{|}~^.-]+@[A-Za-z0-9.-]+$/, "gm");
        const isValidEmail = emailRegex.test(email);
        if(isValidEmail){
          // document.querySelector(".emailErr").style.display ="none";
        }else{
          document.querySelector(".emailErr").style.display ="block";
        }

      if(password.length >= 8){
        let a=  document.querySelector(".passwordErr").style.display = "none";
        // pass = true;
      }else{
        let a=  document.querySelector(".passwordErr");
        a.style.display ="block";
        a.color ="red";
      }
      if(cpassword != password){
           document.querySelector(".cpasswordErr").style.display ="block";

      }else{
        // cpass=true;
        document.querySelector(".cpasswordErr").style.display ="none";
      }
       

    }
     return(
        <form className="form" onSubmit={submitForm}>
        <p className="title">Email</p>
        <input type="email"  value={email}  onChange={(e) => setEmail(e.target.value)} />
        <p className="emailErr">Invaild email format</p>
        
        <p className="title">Password</p>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} id="password"/>
        <p className="passwordErr">Password must be at least 8 characters</p>
        <div className="title"><p>Confirm Password</p></div>
        <input type="password" value={cpassword}  onChange={(e) => setCpassword(e.target.value)} />
        <p className="cpasswordErr">password do not match</p>
        <button type="submit>">Sing Up</button>
        </form>
     )
}

export default Koti;
