import React from "react";
import { useState } from "react";


const Koti = ()=>{
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    console.log(password);
    const [cpassword, setCpassword] = useState("");
    const [isValidEmail, setIsValidEmail] = useState(false);
    const [isPassword, setIsPassword] = useState(false);
    const [isCpassword, setIsCpassword] = useState(false);
    // let pass = false, cpass =false;
   
    const handelEmail =(e)=>{
          setEmail(e.target.value);
          const emailRegex =
          new RegExp(/^[A-Za-z0-9_!#$%&'*+\/=?`{|}~^.-]+@[A-Za-z0-9.-]+$/, "gm");
          
          setIsValidEmail(emailRegex.test(e.target.value));
    } 
    const  valpassword = (e) =>{
          setPassword(e.target.value);
          if(password>=8){
            setIsPassword(true)
          }

    }
 
    function submitForm (e){
        e.preventDefault();
        // if(isValidEmail && pass && cpass){
          // alert("hell")
        // }
      
        // if(isValidEmail){
        //   // document.querySelector(".emailErr").style.display ="none";
        // }else{
        //   document.querySelector(".emailErr").style.display ="block";
        // }
        

      
       

    }
     return(
        <form className="form" onSubmit={submitForm}>
        <p className="title">Email</p>
        <input type="email"  value={email}  onChange={handelEmail} />
      
        {!isValidEmail&&(
          <p >Invaild email format</p>
        )}
        
        <p className="title">Password</p>
        <input type="password" value={password} onChange={valpassword} id="password"/>
        <p className="passwordErr">Password must be at least 8 characters</p>
        <div className="title"><p>Confirm Password</p></div>
        <input type="password" value={cpassword}  onChange={(e) => setCpassword(e.target.value)} />
        <p className="cpasswordErr">password do not match</p>
        <button type="submit>">Sing Up</button>
        </form>
     )
}

export default Koti;
